import React, { Component } from "react";
import Conditional from "./Conditional";

class App extends Component {
    constructor() {
        super();
        this.state = {
            loggedIn : false
        }
        this.logIn = this.logIn.bind(this);
    }
    logIn() {
        console.log("Logging in");
        this.setState( () => {
            loggedIn ? (loggedIn : true) : (loggedIn : false)
        })
    }

    render() {
        return (
            <div>
                {this.state.loggedIn ? <h1>You're logged in!</h1> : <Conditional />}

                <button onClick={this.logIn}>{loggedIn ? "Log in" : "Log out"}</button>
            </div>
        )
    }
};

export default App;
