import React, { Component } from "react";
import Conditional from "./Conditional";

class App extends Component {
    constructor() {
        super();
        this.state = {
            loggedIn: false
        }
        this.logIn = this.logIn.bind(this);
    }

    logIn() {
        this.setState(prevState => {
            return {
                loggedIn: !prevState.loggedIn
            }
        })
    }

    render() {
        let loggedText = this.state.loggedIn ? "Log out" : "Log in"
        return (
            <div>
                {this.state.loggedIn ? <h1>You're logged in!</h1> : <Conditional />}

                <button onClick={this.logIn}>{loggedText}</button>
            </div>
        )
    }
};

export default App;
