import React, { Component } from "react";
import Conditional from "./Conditional";

class App extends Component {
    constructor() {
        super();
        this.state = {
            loggedIn: false
        }
        this.logIn = this.logIn.bind(this);
    }
    logIn() {
        console.log(this.state.loggedIn)
        if (!this.loggedIn) {
            this.setState(() => {
                return {
                    loggedIn: true
                }
            })
        } else {
            this.setState(() => {
                return {
                    loggedIn: false
                }
            })
        }
    }

    render() {
        return (
            <div>
                {this.state.loggedIn ? <h1>You're logged in!</h1> : <Conditional />}

                <button onClick={this.logIn}>{this.loggedIn ? "Log out" : "Log in"}</button>
            </div>
        )
    }
};

export default App;
